
const connect = require('../data').connect;
const metaUser = require('../data').metaUser;

function createUser(user) {
    metaUser.validate(user);
}

class UserService {
    constructor(database) {
        this.db = database;    
    }

    createUser(user) {
        metaUser.validate(user);
    }
}

module.exports = {
    UserService: UserService
};
